import React, { useState, useEffect } from 'react';

const App = () => {
  const [userData, setUserData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);






  const cardContainerClasses = "flex flex-wrap justify-center items-center p-4";
  const cardClasses = "max-w-xs mx-2 my-4 bg-white dark:bg-zinc-800 shadow-lg rounded-lg overflow-hidden";
  const imageClasses = "w-full h-40 object-cover";
  const titleClasses = "text-lg font-bold text-zinc-800 dark:text-white";
  const textClasses = "text-zinc-600 dark:text-zinc-400 mt-2";
  const buttonClasses = "bg-blue-500 text-white px-4 py-2 mt-4 rounded-lg";

  const Card = () => {
      return (
          <div className={cardContainerClasses}>
              <div className={cardClasses}>
                  <img src="https://placehold.co/300x200" alt="Placeholder" className={imageClasses} />
                  <div className="p-4">
                      <h2 className={titleClasses}>Card Title</h2>
                      <p className={textClasses}>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                      <button className={buttonClasses}>Read more</button>
                  </div>
              </div>
          </div>
      );
  };





  const fetchData = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('https://randomuser.me/api/');
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      const data = await response.json();
      setUserData(data.results[0]); // Hanya mengambil satu pengguna
      setIsLoading(false);
    } catch (error) {
      setError(error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []); // Fetch data saat komponen dimuat

  const handleRefresh = () => {
    fetchData(); // Panggil fungsi fetchData untuk mendapatkan data baru
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      <h1>User Data:</h1>
      {userData && (
        <div>
          <img src={userData.picture.large} alt="User" />
          <p>Name: {userData.name.first} {userData.name.last}</p>
          <p>Email: {userData.email}</p>
          <p>Phone: {userData.phone}</p>
        </div>
      )}
      <button onClick={handleRefresh}>Refresh</button> {/* Tambahkan tombol refresh */}
    </div>
  );
};

export default App;
